var menu=document.getElementById('menu');
			var p=menu.getElementsByTagName('p');
			var ul=menu.getElementsByTagName('ul');
			var ulList=[];
			for (var i=0;i<p.length;i++) {
				ulList.push(ul[i]);
				//给每个p加上点击事件
				p[i].index=i;
				p[i].onclick=function(){
					var that=this.index;
					//循环所有的ul只展开一个
					for(var h=0;h<ulList.length;h++){
						if(h!=that){
							//隐藏ul索引与当前不匹配的ul列表
							ulList[h].style.display='none';
							p[h].className='';
						}else{
							//如果当前的ul是关闭的，则展开，否则关闭
							if(this.className==''){
								ul[that].style.display='block';
								p[that].className='active';
							}else{
								ul[that].style.display='none';
								p[that].className='';
							}
						}
					}
				}
			}
			//为每个li加上点击事件,添加背景色
			var liArr=[];
			for (var j=0;j<ul.length;j++) {
				//循环所有ul下的li,放到数据组里，并为所有的li加上点击事件
				var ali=ul[j].getElementsByTagName('li');
				for(var k=0;k<ali.length;k++){
					liArr.push(ali[k]);
					ali[k].onclick=function(){
						for (var m=0;m<liArr.length;m++) {
							if(liArr[m]!=this){
								liArr[m].className='';
							}
						}
						if (this.className=='') {
							this.className='current';
						} else{
							this.className='';
						}
					}
				}
			}